using UnityEngine;using System.Collections;public class TargetSpawnTimer : Timer {    public int spawnPosition = 0;    protected override void OnTimeUp()    {        if (!GameManager.Instance.player.HasTarget(spawnPosition))        {            Target newTarget = TargetManager.Instance.CreateRandomTarget(GameManager.Instance.CalculateLocalPosition(spawnPosition));            GameManager.Instance.player.ReplaceTarget(spawnPosition, newTarget);        }        base.OnTimeUp();    }}